                'content' => '{"type":"doc","content":[{"type":"heading","attrs":{"level":2,"textAlign":null},"content":[{"text":"Étape 1 : Se Former et Écouter","type":"text"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","attrs":{"textAlign":null},"content":[{"text":"Action","type":"text","marks":[{"type":"bold"}]},{"text":" : Participer à des formations sur la santé mentale au travail.","type":"text"}]}]},{"type":"listItem","content":[{"type":"paragraph","attrs":{"textAlign":null},"content":[{"text":"Objectif","type":"text","marks":[{"type":"bold"}]},{"text":" : Développer une compréhension des signes de détresse et des stratégies d\'écoute active.","type":"text"}]}]},{"type":"listItem","content":[{"type":"paragraph","attrs":{"textAlign":null},"content":[{"text":"Résultat attendu","type":"text","marks":[{"type":"bold"}]},{"text":" : Créer un climat de confiance où les employés se sentent compris.","type":"text"}]}]}]},{"type":"heading","attrs":{"level":2,"textAlign":null},"content":[{"text":"Étape 2 : Créer un Environnement Positif","type":"text"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","attrs":{"textAlign":null},"content":[{"text":"Action","type":"text","marks":[{"type":"bold"}]},{"text":" : Mettre en place des pratiques de reconnaissance et de feedback constructif.","type":"text"}]}]},{"type":"listItem","content":[{"type":"paragraph","attrs":{"textAlign":null},"content":[{"text":"Objectif","type":"text","marks":[{"type":"bold"}]},{"text":" : Encourager une communication ouverte et renforcer la confiance.","type":"text"}]}]},{"type":"listItem","content":[{"type":"paragraph","attrs":{"textAlign":null},"content":[{"text":"Résultat attendu","type":"text","marks":[{"type":"bold"}]},{"text":" : Réduction du stress et amélioration du bien-être mental.","type":"text"}]}]}]},{"type":"heading","attrs":{"level":2,"textAlign":null},"content":[{"text":"Étape 3 : Promouvoir la Flexibilité","typ